class Solution {
public:
    int dp[500][500];
    int helper(vector<int>& nums,int i,int j){
        if(i>j)
            return 0;
        if(dp[i][j]!=-1)
            return dp[i][j];
        int ans=0;
        for(int k=i;k<=j;k++){
            int curr=nums[i-1]*nums[k]*nums[j+1];
            ans=max(ans,helper(nums,i,k-1)+helper(nums,k+1,j)+curr);
        }
        dp[i][j]=ans;
        return ans;
    }
    int maxCoins(vector<int>& nums) {
        nums.insert(nums.begin(),1);
        nums.push_back(1);
        memset(dp,-1,sizeof(dp));
        return helper(nums,1,nums.size()-2);
    }
};
