        if(dp[i][j] != -1) return dp[i][j];
        int ans = INT_MIN;
        for(int k = i; k<=j-1; k++){
            int temp = solve(nums,i,k) + solve(nums,k+1,j) + nums[k]*nums[i-1]*nums[j];
            ans = max(ans,temp);
