    int sum[][] = new int[helpNum.length][helpNum.length];
    for(int gap = 0; gap < nums.length; gap++) {
        for(int begin = 1; begin < nums.length - gap + 1; begin++) { // we don't consider the dummy head and tail
            int end = begin + gap;
            sum[begin][end] = Integer.MIN_VALUE;
