```
class Solution {
    public int maxCoins(int[] nums) {
        int n = nums.length;
        if(n==0) return 0;
        if(n==1) return nums[0];

        int[][] memo = new int[n][n];

        //memo[i][j] = maximum coins obtained to brust ballons from index i to j in nums.
        for(int len=1;len<=n;len++){
            for(int st=0,end=st+len-1;end<n;st++,end++){
                //Lets find maximum coins if kth balloon is bursted last
                for(int k=st;k<=end;k++){
                    //Kth balloon will be burst last only if,
                    //Balloons from st to k-1 & k+1 to end are bursted before kth balloon
                    int cost= ((k==st)?0:memo[st][k-1])+ ((k==end)?0:memo[k+1][end]);
                    //As Kth balloon is burst last, start-1 & end+1 will be its adjacent balloons
                    cost+=((st==0)?1:nums[st-1])*nums[k]*((end==n-1)?1:nums[end+1]);
                    //Store maximum coins 
                    memo[st][end]=Math.max(memo[st][end],cost);
                }
            }
        }
        return memo[0][n-1];
    }
}
