class Solution {
public:

    int cut(vector<int> &b, int left, int right){
        if(left+1==right) return 0;
        int ans=0;
        for(int i=left+1;i<right;i++){
            int t1=cut(b,left,i);
            int t2=cut(b,i,right);
            int tans=b[left]*b[i]*b[right];
            ans=max(ans,tans+t1+t2);
