            int curr_val = nums[i-1] * nums[k] * nums[j] + temp;
            if(curr_val > curr_sum) {
                curr_sum = curr_val; 
            }
        }
        return dp[i][j] = curr_sum; 
    }

    int maxCoins(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        nums.insert(nums.begin(),1); 
        nums.push_back(1);  
        return solve(nums, 1, nums.size()-1); 
    }

