class Solution {
int max = 0;
    public int maxCoins(int[] nums) {
        if(nums.length == 0){
            return 0;
        } 
        if(nums.length == 1){
            return nums[0];
        }
        List<Integer> list = new ArrayList<Integer>();
        for(int x: nums){
            list.add(x);
        }
        backtrack(list, 0);
        return max;
    }

    private void backtrack(List<Integer> temp, int curCount){
        if(temp.isEmpty()){
            max = Math.max(curCount, max);
        } else {
            int n = temp.size();
            for(int i = 0; i < n; i++){
                if(i == 0){
                    int tempInt = temp.remove(0);
                    if(n - 1 == 0){
                        backtrack(temp, curCount + tempInt);
                    } else {
                        backtrack(temp, curCount + tempInt * temp.get(0));
                    } 
                    temp.add(0, tempInt);
                } else if(i == n - 1){
                    int tempInt = temp.remove(n - 1);
                    backtrack(temp, curCount + temp.get(n - 2) * tempInt);
                    temp.add(n - 1, tempInt);
                } else { 
                    int tempInt = temp.remove(i);
                    backtrack(temp, curCount + tempInt * temp.get(i - 1) * temp.get(i));
                    temp.add(i, tempInt);
