    int ans = INT_MIN;
    int first = (si-1>=0)?nums[si-1] : 1;
    int last = (ei+1<n)?nums[ei+1] : 1;

    for(int i = si;i<=ei;i++){
        int before = (i==si)? 0 : BurstBalloonsTD(nums,si,i-1,strg);
        int after = (i==ei)? 0 : BurstBalloonsTD(nums,i+1,ei,strg);

        int total = before + after + first*last*nums[i];
        ans = max(ans,total);
    }

    return strg[si][ei] = ans;
}
