* ***Using Reacursion && Memoization***

* ***Time Complexity :- O(N * N * N)***

* ***Space Complexity :- O(N * N)***

```
class Solution {
public:

    // declare dp

    int dp[305][305];

    int helper(vector<int>& arr, int start, int end)
    {
        // base case, if there is no. element remaining in partition

        if(start > end)
        {
            return 0;
        }

        // if already calculated

        if(dp[start][end] != -1)
        {
            return dp[start][end];
        }

        int maxi = 0;

        // start deleting the every element and take maximum of it

        for(int j = start; j <= end; j++)
        {
            // call for deleting left part [start, j - 1]

            int left = helper(arr, start, j - 1);

            // call for deleting right part [j + 1, end]

            int right = helper(arr, j + 1, end);

            // take the cost of deleting the curr element

            // all the elements between [start, j - 1] is deleted, so left adjacent will be at start - 1

            // all the elements between [j + 1, end] is deleted, so right adjacent will be at [end + 1]

            int prod = arr[start - 1] * arr[j] * arr[end + 1];

            // update maxi

            maxi = max(maxi, prod + left + right);
        }

        // store the res and return

        return dp[start][end] = maxi;
    }

    int maxCoins(vector<int>& nums) {

        int n = nums.size();

        // insert 1 and back

        nums.push_back(1);

        // insert 1 at start

        nums.insert(nums.begin(), 1);

        // initialize dp with -1

        memset(dp, -1, sizeof(dp));

        return helper(nums, 1, n);
    }
};
```

