int solve(int i, int j, vector<int> &nums, vector<vector<int>> &dp) {
	if(i > j) return 0;
	if(dp[i][j] != -1) return dp[i][j];
	int result = INT_MIN;
	for(int k = i; k <= j; k++) {
		int temp = nums[i - 1] * nums[k] * nums[j + 1] + 
					solve(i, k - 1, nums, dp) + solve(k + 1, j, nums, dp);
		result = max(result, temp);
	}
	return dp[i][j] = result;
}

