int maxCoins(vector<int>& nums) {
	nums.insert(nums.begin(), 1);
	nums.push_back(1);
	int n = nums.size();
	vector<vector<int>> dp(n, vector<int>(n - 1, 0));
	for(int x = 1; x < n - 1; x++) {
		int i = 1, j = x;
		while(j < n - 1) {
			int result = INT_MIN;
			for(int k = i; k <= j; k++) {
				int temp = nums[i - 1] * nums[k] * nums[j + 1] + 
							dp[i][k - 1] + dp[k + 1][j];
				result = max(result, temp);
			}
			dp[i][j] = result;
			i++;
			j++;
		}
	}
	return dp[1][n - 2];
}
