class Solution {
    public int maxCoins(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n+1][n+1];
        for(int i = 0 ; i <= n ; i++)
            for(int j = 0 ; j <= n ; j++)
                dp[i][j] = -1;
        int[] arr = new int[n+2];
        for(int i = 1 ; i <= n ; i++)
        {
            arr[i] = nums[i-1];
        }
        arr[0] = 1;
        arr[n+1] = 1;

        return coins(arr , 1 , n , dp);
    }

    int coins(int[] arr , int i , int j , int[][] dp){
        if(i > j)   return 0;
        if(dp[i][j] != -1)
            return dp[i][j];

        for(int k = i ; k <= j ; k++)
        {
            dp[i][j] = (int)Math.max(dp[i][j] , arr[i-1]*arr[j+1]*arr[k] + coins(arr , i , k-1 , dp) + coins(arr , k+1 , j , dp));
        }

        return dp[i][j];
    }
}
