    class Solution {
        public int maxCoins (int[] nums) {
            int n = nums.length;
            int[][] dp = new int[n + 2][n + 2];
            int[] arr = new int[n + 2];
            arr[0] = 1;
            arr[arr.length - 1] = 1;
            for (int i = 1; i < arr.length - 1; i++) {
                arr[i] = nums[i - 1];
            }
            for (int i = n; i >= 1; i--) {
                for (int j = 1; j <= n; j++) {
                    if (i > j) continue;
                    int maxCoins = Integer.MIN_VALUE;
                    for (int k = i; k <= j; k++) {
                        int cost = (arr[i - 1] * arr[k] * arr[j + 1]) + dp[i][k - 1] + dp[k + 1][j];
                        maxCoins = Math.max(maxCoins, cost);
                    }
                    dp[i][j] = maxCoins;
                }
            }
            return dp[1][n];
        }
    }
