class Solution {
public:
    int dp[305][305]; // Declare DP

    int f(vector<int> &arr, int i, int j) {
        if (j - i + 1 <= 2) return 0;
        if (dp[i][j] != -1) return dp[i][j]; // Check DP state
        int money = INT_MIN;
        for (int k = i + 1; k < j; k++) {
            int curMoney = (arr[i] * arr[k] * arr[j]) + f(arr, i, k) + f(arr, k, j);
            money = max(money, curMoney);            
        }
        return dp[i][j] = money; // Save result in DP
    }

    int maxCoins(vector<int>& nums) {
        memset(dp, -1, sizeof(dp)); // Initialize DP
        nums.push_back(1);
        nums.insert(nums.begin(), 1);
        return f(nums, 0, nums.size() - 1);
    }
};
