class Solution {
public:
    int res=0;
    int n=0;
    unordered_map<int,bool> busted;
    void fun(vector<int>& nums, int coins){
        res=max(res,coins);
        for(int i=0;i<n;i++){
            if(busted[i]==1){
                continue;
            }
            busted[i]=1;
            int currcoinbusted=nums[i];
            int j=i-1;
            while(j>=0){
                if(busted[j]==0){
                    currcoinbusted*=nums[j];
                    break;
                }
                j--;
            }
            j=i+1;
            while(j<n){
                if(busted[j]==0){
                    currcoinbusted*=nums[j];
                    break;
                }
                j++;
            }
            fun(nums,coins+currcoinbusted);
            busted[i]=0;
        }
    }
    int maxCoins(vector<int>& nums) {
        n=nums.size();
        fun(nums,0);
        return res;
    }
};
