            int j=i-1;
            while(j>=0){
                if(busted[j]==0){
                    currcoinbusted*=nums[j];
                    break;
                }
                j--;
            }
            j=i+1;
            while(j<n){
                if(busted[j]==0){
                    currcoinbusted*=nums[j];
                    break;
                }
                j++;
            }
            fun(nums,coins+currcoinbusted);
            busted[i]=0;
        }
    }
    int maxCoins(vector<int>& nums) {
        n=nums.size();
        fun(nums,0);
        return res;
    }
};
