class Solution:
    def threeSumClosest(self, nums, target):
        nums.sort()
        n, ans = len(nums), float("inf")

        for i in range(n):
            beg, end = i + 1, n - 1

            while beg < end:
                sm = nums[beg] + nums[end] + nums[i]
                ans = min(ans, sm, key = lambda x: abs(x - target))

                if sm <= target:
                    beg += 1
                elif sm > target:
                    end -= 1

        return ans
