class Solution {
public:
    int threeSumClosest(vector<int>& nums, int target) {

       int closest= nums[0]+nums[1]+nums[2];
       sort(nums.begin(),nums.end());
        if(nums.size()<3)
        {
            return 0;
        }
       for(int i=0;i<nums.size();i++)
       {
           int j=i+1;
           int k=nums.size()-1;
           while(j<k){
               int sum=nums[i]+nums[j]+nums[k];
               if(sum==target)
               {
                   j++;
                   k--;
               }
               if(abs(target-sum)<abs(target-closest))
               {
                   closest=sum;
               }
               if(sum<target){
                   j++;
               }
               else{
                   k--;
               }
           }
       }

        return closest;
    }
};
