class Solution {
    public int threeSumClosest(int[] nums, int target) {
        Arrays.sort(nums);
        int output = nums[0] + nums[1] + nums[nums.length - 1];
        for(int i = 0; i < nums.length - 2; i++)
        {
            int j = i + 1;
            int k = nums.length - 1;
            while(j < k)
            {
                int currSum = nums[i] + nums[j] + nums[k];
                if(currSum > target)
                    k--;
                else
                    j++;

                if(Math.abs(currSum - target) < Math.abs(output - target))
                    output = currSum;
            }
        }
        return output;
    }
}
