    int threeSumClosest(vector<int>& nums, int target) {
        sort(nums.begin(),nums.end());

        int comp=INT_MAX,n=nums.size(),res=0;

        for(int i=0; i<n-2; i++)
        {
            int j = i+1, k = n-1;

            while(j<k)
            {
                int sum = nums[i]+nums[j]+nums[k];
                if(comp > abs(sum-target))
                {
                    comp = abs(sum-target);
                    res = sum;
                }

                if(sum < target)
                {
                    j++;
                }
                else if(sum > target)
                {
                    k--;
                }
                else
                {
                    return res;
                }
            }
            while(nums[i+1] == nums[i] && i+1 < n) i++;
        }
        return res;
    }
};


