    int threeSumClosest(vector<int>& nums, int target) {


         int ans =0 ;
         int minima= INT_MAX;
        int n =nums.size();
        int result=0;  

      sort(nums.begin() ,nums.end()); //sorting array to use two pointer approach 


      for( int i = 0; i< n-1; i++)
      { 


          int sum = nums[i];
           int p1 =i+1;
           int p2= n-1 ;

         while(p1<p2)
          {


            sum =nums[i]+nums[p1]+nums[p2];    

            int diff =target-sum;


             if(mod(diff) < mod(minima) )  /////for finding nearst greater or lower 
             {

                 minima=diff;
                  result= sum;
             }

            if(sum > target)
            {
                 p2--;
                 continue;
            }

            if(sum <target )
            {
                p1++;
                continue;
            }


            ans++; ///// if the taget hit we return it 
               p1++;
            p2--;
              break; 

          }


      }

     if(! ans)
     {
          return result;
     }  


      return target;
      }
};

