// Iterative Space O(1) Solution
public void connect(TreeLinkNode root) {
    for(TreeLinkNode lead = root; lead != null; lead = lead.left)
        for(TreeLinkNode cur = lead; cur != null; cur = cur.next){
            if(cur.left != null) cur.left.next = cur.right;
            if(cur.right != null && cur.next != null) cur.right.next = cur.next.left;
        }
}

// Iterative BFS Solution
public void connect(TreeLinkNode root) {
    Queue<TreeLinkNode> queue = new LinkedList<TreeLinkNode>();
    if(root != null) queue.offer(root);
    TreeLinkNode next = null, cur = null;
    for(int n = 1; !queue.isEmpty(); n *= 2, next = null, cur = null)
        for(int i = 0; i < n; i++){
            cur = queue.poll();
            cur.next = next;
            next = cur;
            if(cur.right != null){
                queue.offer(cur.right);
                queue.offer(cur.left);
            }
        }
}

