class Solution {
public:
    vector<vector<int>> ans;
    void solve(int k , int n,int sum , vector<int> v , int start)
    {
        if(k == 0)
        {
            if(sum == n)
                ans.push_back(v);
            return;
        }
        for(int i=start;i<=9;i++)
        {
            v.push_back(i);
            solve(k - 1 , n , sum+i , v , i + 1);
            v.pop_back();
        }
    }
    vector<vector<int>> combinationSum3(int k, int n) {
        vector<int> v;
        solve(k , n , 0, v , 1);
        return ans;
    }
};
