    def generatePalindromes(self, s: str) -> List[str]:
        def dfs(freq, fst_half, mid, res):
            if sum(freq.values()) == 0:
                res.append(fst_half + mid + fst_half[::-1])
                return
            for char in freq:
                if freq[char] > 0:
                    freq[char] -= 1
                    dfs(freq, fst_half + char, mid, res)
                    freq[char] += 1

        freq = Counter(s)
        single = [char for char in freq if freq[char] % 2 == 1]
        if len(single) > 1:
            return []
        mid = single[0] if single else ''
        for char in freq:
            freq[char] //= 2
        res = []
        dfs(freq, '', mid, res)
        return res
