class Solution {
public:
    vector<vector<int>> combinationSum3(int k, int n) {
        vector<vector<int>> ans;
        vector<bool> bits;
        static vector<int> vals;
        if(vals.empty())for(int i=0;i<9;i++)vals.push_back(i+1);
        int i;
        bits.resize(9);
        for(i=0;i<k;i++)
            bits[8-i]=1;
        do{
            int now=inner_product(bits.begin(),bits.end(),vals.begin(),0);
            if(now==n){
                ans.push_back({});
                for(i=0;i<9;i++)
                    if(bits[i])ans.back().push_back(i+1);
            }
        }while(next_permutation(bits.begin(),bits.end()));
        return ans;
    }
};
