func combinationSum3(k int, n int) [][]int {
    result := make([][]int, 0)

    backtrack(k, n, 1, []int{}, &result)

    return result
}

func backtrack(k, target, start int, visited []int, result *[][]int) {
    if k == 0 && target == 0 {
        nums := make([]int, 0)
        nums = append(nums, visited...)
        *result = append(*result, nums)
        return
    }

    for i := start; i <= 9; i++ {
        visited = append(visited, i)
        backtrack(k-1, target-i, i+1, visited, result)
        visited = visited[:len(visited)-1]
    }
}
