// Backtracking
// O(9^k)/O(1) O(k)/O(1)
public List<List<Integer>> combinationSum3(int k, int n) {

	List<List<Integer>> ans = new ArrayList<>();
	List<Integer> temp = new ArrayList<>();
	boolean[] map = new boolean[10];
	combinationSum3Helper(k, n, map, 0, ans, temp, 0);
	return ans;
}

// Backtracking
// O(9^k)/O(1) O(k)/O(1)
public void combinationSum3Helper(int k, int n, boolean[] map, int curr, List<List<Integer>> ans,
		List<Integer> temp, int last) {

	if (k == 0 && curr == n) {
		ans.add(new ArrayList<>(temp));
		return;
	} else if (k == 0 && curr != n)
		return;

	for (int i = last + 1; i <= 9; i++) {
		if (!map[i]) {
			map[i] = true;
			temp.add(i);
			combinationSum3Helper(k - 1, n, map, curr + i, ans, temp, i);
			map[i] = false;
			temp.remove(temp.size() - 1);
		}
	}
}

