class Solution {
public:
    vector<vector<int>> result;

    void solve(int k , int n , vector<int> current , int index , int sum){

        if(k==0 and n==0){  //base case 1 : if k==0 and sum is exhausted than we have our required solution
            result.push_back(current); 
            return ;                
        }

        if(k==0){        //base case 2: if k is exhausted but the sum is less or more , return from here
            return ;
        }

        for(int i=index ;i<=9 ; i++){  //choose numbers from 1 to 9

            if(n<=sum){  //if the current sum is less than the required sum than only include the element
                current.push_back(i);
                solve(k-1,n-i,current,i+1,sum); //subtract k by 1 , subtract current sum by included element , check for next element
            }

            current.pop_back(); //backtrack from here as either our current sum has exceeded the sum required or we have reached our solution and we want to explore more possibilites so remove last element  from the current vector
        }


    }

    vector<vector<int>> combinationSum3(int k, int n) {

        if(k==0 || n==0) return {};  //if k==0 or n==0 return 0
        int sum = n;
        solve(k,n,{},1,sum);

        return result;
    }
};

