type VVI = Vec<VI>;
impl Solution {
    pub fn combination_sum3(K: i32, T: i32) -> VVI {
        let mut paths = vec![];
        fn go(start: i32, k: i32, t: i32, path: &mut VI, paths: &mut VVI) {
            if k == 0 {
                if t == 0 {
                    paths.push(path.clone());
                }
                return;
            }
            for x in start..=9 {
                path.push(x);
                go(x + 1, k - 1, t - x, path, paths);
                path.pop();
            }
        }
        go(1, K, T, &mut vec![], &mut paths);
        return paths;
    }
}
