vector<vector<int>> combinationSum3(int k, int n) 
{
	vector<vector<unordered_set<int>>> dp(n + 1, 
		vector<unordered_set<int>>());

	dp[0] = { unordered_set<int>() };

	for (int i = 1; i <= 9; ++i)
		for (int j = i; j <= n; ++j)
			for (auto set : dp[j - i])
				if (set.size() < k && !set.count(i))
					set.insert(i), dp[j].push_back(set);

	vector<vector<int>> result;

	for (auto set : dp[n])
		if (set.size() == k)
			result.push_back(vector<int>(set.begin(), set.end()));

	return result;
}
```

