        for (int i = n -1; i >=0 ; --i){
            masks[i] = masks[i+1] | (1 << (s[i] - 'a'));
        }
        string ret;
        int visited = 0;
        for (int i = 0; i < n; ++i){
            while (!ret.empty() && (!seen(visited, s[i]))&& ret.back() > s[i]){
                // if the back is not the last element
                if (masks[i+1] & (1 << (ret.back() - 'a'))){
                    visited ^= (1 << (ret.back() - 'a'));
                    ret.pop_back();
                } else {
                    break;
                }  
            }
            if (!(seen(visited, s[i]))){
                visited |= (1 << (s[i] - 'a'));
                ret.push_back(s[i]);
            }

        }
        return ret;

    }
};
```

