class Solution:
    def removeDuplicateLetters(self, s: str) -> str:
        stack = []
        last_occurrence = {char: i for i, char in enumerate(s)}

        for i, char in enumerate(s):
            if char in stack:
                continue

            while stack and char < stack[-1] and i < last_occurrence[stack[-1]]:
                stack.pop()

            stack.append(char)

        return ''.join(stack)

