            charStack.push(c);
            inStack[c - 'a'] = true;
                      }

    // Construct the result string by popping characters from the stack
    string result = "";
    while (!charStack.empty()) {
        result = charStack.top() + result;
        charStack.pop();
    }

    return result;
   }
};
