class Solution {
public:
    string removeDuplicateLetters(string s){

        unordered_map<char, int>last; //Map to store last index of every char
        vector<bool>vis(26);          //Vector to check if the character is visited
        string ans = "";

        for(int i = 0; i < s.size(); i++)
            last[s[i]] = i;         //Storing the last index of each character

        for(int i = 0; i < s.size(); i++){
            if(vis[s[i] - 'a']) continue;   //If index is visited, pass


            //If s[i] is smaller than last character of ans and last index of the 
            //character is greater than i, we can delete the last character of
            //ans as it will come after i later
            while(!ans.empty() && ans.back() > s[i] && last[ans.back()] > i){
                vis[ans.back() - 'a'] = false;      
                ans.pop_back();
            }
            ans.push_back(s[i]);            //Adding the current character to ans
            vis[s[i] - 'a'] = true;         //Marking the current node visited
        }
        return ans;
    }
