            count[ord(c) - ord('a')] -= 1
            if used[ord(c) - ord('a')] == 0:
                while stack and c < stack[-1] and count[ord(stack[-1]) - ord('a')]:
                    used[ord(stack[-1]) - ord('a')] = 0
                    stack.pop()
                stack.append(c)
                used[ord(c) - ord('a')] = 1
        return ''.join(stack)
