            if(present[s[i]-97]) continue;
            while(!st.empty() && s[i]<st.top() && freq[(st.top())-97]>0){
                present[st.top()-97]=0;
                st.pop();
            }
            st.push(s[i]);
            present[s[i]-97]=1;
        }

        stack<char> temp;
        while(!st.empty()) {
            temp.push(st.top());
            st.pop();
        }

        string ans="";
        while(!temp.empty()){
            ans+=temp.top();
            temp.pop();
        }
        return ans;
    }
};
