        Stack<Character>st=new Stack<>();
        for(char c : ch) {
            res[c - 'a']++;
        }
        int index=0;
        for(char c : ch) {
            index = c-'a';
            res[index]--;
            if(visited[index])
                continue;
            while(!st.isEmpty() && c<st.peek() && res[st.peek()-'a']>0) {
                visited[st.pop() - 'a'] = false;
            }
            st.push(c);
            visited[c-'a'] = true;
        }
        StringBuilder sb = new StringBuilder();
        while(!st.isEmpty()) {
            sb.insert(0,st.pop());
        }
        return sb.toString();
    }

