```
class Solution {
public: 

    string removeDuplicateLetters(string s) { 

        //TRACK OF ELEMENTS WHICH ARE INSTACK
        unordered_map<char, bool> visited; 

        //TRACK FREQUENCY OF ELEMENTS
        unordered_map<char, int> frequency; 

        //INITALLY STACK IS EMPTY MARK VISITED AS FALSE, SET FREQUENCY 
        for (int i = 0; i < s.length(); i++) {  
            visited[s[i]] = false;
            frequency[s[i]]++;
        }

        //STACK - WE ARE GOING TO STORE ELEMENTS IN DESENDING ORDER IN STACK
        stack<char> st; 

        //..INITALLY PUSH FIRST ELEMENT AS STACK
        st.push(s[0]);   

        //AS ELEMENT IS IN STACK MARK IT AS TRUE IN VISITED MAP
        visited[s[0]] = true;

        //ITERATE 1...N-1
        for (int i = 1; i < s.length(); i++) { 

            //IF INCOMING CHAR IS LEXICOGRAPHICALLY LESSER THAN TOP OF STACK
            if (s[i] <= st.top()) { 

                //AND THAT ELEMENT ISN'T IN STACK(WE DON'T NEED DUPLICATES)
                if (!visited[s[i]]) { 

                    //POP ELEMENTS TILL WE DONT FIND ST.TOP > S[I] (MAINTAING DESENDING ORDER) 
                    //ELEMENTS SHOULD ONLY BE POPED IF THERE FREQ > 1 - BECAUSE WE KNOW THAT THAT CHARECTER IS PRESENT                           //FURTHER IN THE STRING
                    while (!st.empty() and s[i] < st.top() and frequency[st.top()] > 1) { 

                        frequency[st.top()]--; // DECREMMENT ITS FREQ BY ONE (WE SKIPPED THAT CHAR)  
                        visited[st.top()] = false; // SET ELEMENT AS FALSE BEACUSE NOW THEY ARE NOT IN STACK
                        st.pop();  

                    } 

                    st.push(s[i]); //PUSH THE CURRENT ELEMENT
                    visited[s[i]] = true; //SET IT AS TRUE
                }  

                else {  
                    //IF INCOMING ELEMENT IS ALREADY VISITED JUST SKIP IT BY DECREMMENTING ITS FREQUENCY
                    frequency[s[i]]--;
                }
            } 

            //IF INCOMING CHAR > TOP THEN DIRECTLY PUSH IT(MAINTAIN DESCENDING ORDER)
            else { 

                if (!visited[s[i]]) { 
                    st.push(s[i]); 
                    visited[s[i]] = true;
                }
            }
        }

        string answer = ""; 

        //EMPTY THE STACK AND CONCATINATE TO STRING
        while (!st.empty()) {  
            cout << st.top();
            answer += st.top(); 
            st.pop();
        } 

        //REVERSE STRING BEACUSE WE HAVE MAINTAINED DESCENDING IRDER 
        reverse(answer.begin(), answer.end()); 

        return answer;
    }
};
```

