        for (char c: s.toCharArray()) {
            characterFrequencyMap.put(c, characterFrequencyMap.getOrDefault(c, 0) + 1);
        }

        // Remove duplicates while maintaining lexo order
        Stack<Character> stack = new Stack<>();
        Set<Character> stackCharacterSet = new HashSet<>();
        for (char c : s.toCharArray()) {
            while (!stack.isEmpty() && characterFrequencyMap.get(stack.peek()) > 1 && c < stack.peek() && !stackCharacterSet.contains(c)) { // Edge case 1a: !stackCharacterSet.contains(c) -> Input: "cdadabcc", Output:"abc", Expected:"adbc"
                char poppedCharacter = stack.pop();
                stackCharacterSet.remove(poppedCharacter);
                characterFrequencyMap.put(poppedCharacter, characterFrequencyMap.getOrDefault(poppedCharacter, 0) - 1); // Edge case 1b: Input: "cdadabcc", Output:"abc", Expected:"adbc"
            }
            if (!stackCharacterSet.contains(c)) {
                stack.push(c);
                stackCharacterSet.add(c);
            } else { // Edge case 2: Input:"bbcaac", Output:"ac", Expected:"bac"
                characterFrequencyMap.put(c, characterFrequencyMap.getOrDefault(c, 0) - 1);
            }
        }

        // Prepare output
        StringBuilder stringBuilder = new StringBuilder();
        for (char c : stack) {
            stringBuilder.append(c);
        }
        return stringBuilder.toString();
    }
}
```

