class Solution {
public:
    string removeDuplicateLetters(string s) {
        unordered_map<char,int> mp; //mapping char into its maximum index
        unordered_map<char,int> in_stack_mp; //in_stack_mp[i]==1 iff char i in deque
        deque<int> q;  //use deque as stack but return the made string by using deque as queue
        int n = s.length();
        for(int i = 0; i<n; i++) mp[s[i]] = i; //figure out maximum index of each character in s
        for(int i = 0; i<n; i++) { 
            char c = s[i];
            if(in_deque_mp[c]==0) {
                while(!q.empty() && c<q.back() && mp[q.back()]>i) {
                    in_deque_mp[q.back()]=0;
                    q.pop_back();
                }
                in_deque_mp[c] = 1;
                q.push_back(c);
            }
        }
        string ans="";
        while(!q.empty()) {
            ans.push_back(q.front());
            q.pop_front();
        }
        return ans;
    }
};
