class Solution {
    public String removeDuplicateLetters(String s) {
        int[] lastIndx = new int[26];
        int sz = s.length();
        for (int indx = 0; indx < sz; indx++) {
            lastIndx[s.charAt(indx) - 'a'] = indx;
        }
        int[] visited = new int[26];
        Stack<Integer> charDiffStack = new Stack<>();
        for (int indx = 0; indx < sz; indx++) {
            int currCharDiff = s.charAt(indx) - 'a';
            if (visited[currCharDiff] != 0) continue;
            while (!charDiffStack.empty() && charDiffStack.peek() > currCharDiff && lastIndx[charDiffStack.peek()] > indx) {
                visited[charDiffStack.peek()] = 0;
                charDiffStack.pop();
            }
            visited[currCharDiff] = 1;
            charDiffStack.push(currCharDiff);
        }
        StringBuilder res = new StringBuilder();
        while (!charDiffStack.empty()) {
            res.append((char) (charDiffStack.peek() + 'a'));
            charDiffStack.pop();
        }
        return res.reverse().toString();
    }
}
