class Solution {
    fun removeDuplicateLetters(s: String): String {
        val map = mutableMapOf<Char, Int>()
        val set = mutableSetOf<Char>()

        for (i in s){
            map[i] = map.getOrDefault(i, 0 ) + 1
        }

        for (i in s){
            map[i] = map[i]!! - 1
            if (!set.contains(i)){
                while(set.isNotEmpty() && i < set.last() && map[set.last()]!! > 0){
                    set.remove(set.last())
                }
                set.add(i)
            }
    }
        return set.joinToString("")
    }
}
