```java
class Solution {
    public String removeDuplicateLetters(String s) {
        // collect char counts
        int[] charCounts = new int[256];
        for(char c : s.toCharArray()) {
            charCounts[c]++;
        }

        boolean[] inStack = new boolean[256];
        Stack<Character> chars = new Stack<>();
        for(char c : s.toCharArray()) {
            charCounts[c]--;

            if(inStack[c]) {
                continue;
            }

            // pop all chars larger than c and will apear later
            while(!chars.isEmpty() && chars.peek() > c) {
                // if a char have not follow, stop
                if(charCounts[chars.peek()] == 0) {
                    break;
                }

                inStack[chars.pop()] = false;
            }

            // push c
            chars.push(c);
            inStack[c] = true;
        }

        StringBuilder sb = new StringBuilder();
        while(!chars.isEmpty()) {
            sb.append(chars.pop());
        }

        return sb.reverse().toString();
