class Solution {
public:

    string removeDuplicateLetters(string s) {
        unordered_map<char, int> lastidx; // stores last occurance of each character in a string.
        int n = s.size();
        for(int i=0; i<n; ++i) lastidx[s[i]] = i; // iterate over s to store last occurance of each character in map.

        vector<int> vis(26, 0); // to check if we have taken the current character before or not.0 means not taken
        vector<char> st; // use vector to simulate stack.

        for(int i = 0; i < n; ++i){
            if(vis[s[i] - 'a']) continue;// if we have already taken the character before just go to next index.
            else {
                while(!st.empty() and st.back() > s[i] and lastidx[st.back()] > i){
                    vis[st.back() - 'a'] = 0; // By subtracting char of 'a', we are converting the char to int.
                    st.pop_back();
                }
                st.push_back(s[i]);
                vis[s[i] - 'a'] = 1;
            }
        }
		// At last, stack contains the required string, so just iterate over it and store ans in string and return.
        string ans;
        for(auto c: st) ans += c;

        return ans;
    }
};

