class Solution {
public:

    int longestValidParentheses(string s) {
        int n = s.length();
        int ans = 0;
        int lc = 0;  //opening bracket count
        int rc = 0;  //closing bracket count
        for(int i = 0; i < n; i++)  //left to right iteration
        {
            if(s[i] == '(') lc++;
            else rc++;
            if(lc == rc) ans = max(ans, 2 * lc);
            else if(rc > lc) lc = rc = 0;
        }
        lc = rc = 0;
        for(int i = n-1; i >= 0; i--)  //right to left iteration
        {
            if(s[i] == '(') lc++;
            else rc++;
            if(lc == rc) ans = max(ans, lc * 2);
            else if(lc > rc) lc = rc = 0;
        }
        return ans;
    }
};

