class Solution:
    def longestValidParentheses(self, s: str) -> int:        
        res, left, right = 0, 0, 0
        for i in range(len(s)): # forward scan
            if s[i] == '(':
                left += 1
            else: # case ')'
                right += 1
                if right > left:
                    left, right = 0, 0
                elif left == right:
                    res = max(left+right, res)

        left, right = 0, 0
        for i in reversed(range(len(s))): # backward scan
            if s[i] == ')':
                right += 1
            else: # case '('
                left += 1
                if left > right:
                    left, right = 0, 0
                elif left == right:
                    res = max(left+right, res)

        return res
