class Solution {
public:
    int longestValidParentheses(string s) {
        stack<int>st;
        int n=s.length();
        for(int i=0;i<n;i++){
            //base conidition
            if(s[i]=='('){
                st.push(i);
            }
            else{
                //Means ')' has appeared
                if(!st.empty()){
                    if(s[st.top()]=='('){
                        st.pop();
                    }
                    else{
                        st.push(i);
                    }
                }
                //If we come to this part means that stack is empty 
                //and this is the first wrong paranthesis
                else{
                    st.push(i);
                }
            }
        }

        //Now we want to give the length of longest valid paranthesis 
        //substring
        int a=n;
        int longest=0;
        //if st is empty mean no wrong paranthesis
        if(st.empty()){
            return a;
        }
        else{
            while(!st.empty()){
                int b=st.top();
                st.pop();
                longest=max(longest,a-b-1);
                a=b;
            }
            longest=max(longest,a);
            return longest;
        }
    }
};
