class Solution {
    typedef long long int ll;
public:
    int longestValidParentheses(string st) {
   stack<pair<char, int>>s;
   int n = st.size();
   for (int i = 0; i < n; i++)
   {
      if (st[i] == ')' and !s.empty() and s.top().first == '(')
      {
         int t = s.top().second;
         st[t] = '*';
         st[i] = '*'; s.pop();
      }
      else s.push({st[i], i});
