class Solution {
public:

    //function to find the maximum lexicographic sequence using monotonic stack implemented
    //via vector. It will give us maximum lexicographic sequence of length k.

    vector<int> maxLex(vector<int> nums, int k) {
        int N = nums.size();
        vector<int> res;

        for(int i=0; i<N; i++) {
            //condition to pop
            //the last condition ensures length k is returned
            while(!res.empty() and nums[i] > res.back() and k - (int)res.size() <= N - i - 1) {
                res.pop_back();
            }
            if(res.size() < k)
                res.push_back(nums[i]);
        }
        return res;
    }

    //function to return true if vector starting at a is lexicographically greater than nums2 starting at b

    bool greater(vector<int>& nums1, vector<int>& nums2, int a, int b) {
        while(a < nums1.size() or b < nums2.size()) {
            if(a >= nums1.size())
                return false;
            else if(b >= nums2.size())
                return true;
            else if(nums1[a] < nums2[b]) 
                return false;
            else if(nums1[a] > nums2[b])
                return true;
            else 
                ++a,++b;
        }
        return true;
    }

    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        vector<int> ans;
        //we are trying to generate all possible max lexicographic sequences from the two vextors
        //of different lengths and then we merge them and compare for maximum result
        for(int i=0; i<=k; i++) {
            int j = k - i;

            if(i > nums1.size() or j > nums2.size())
                continue;

            vector<int> max1 = maxLex(nums1,i);
            vector<int> max2 = maxLex(nums2,j);

            //using merge sort technique
            vector<int> merged;

            int a = 0;
            int b = 0;

            while(a < max1.size() or b < max2.size()) {
                if(a >= max1.size())
                    merged.push_back(max2[b++]);
                else if(b >= max2.size())
                    merged.push_back(max1[a++]);
                else if(max1[a] < max2[b]) 
                    merged.push_back(max2[b++]);
                else if(max1[a] > max2[b])
                    merged.push_back(max1[a++]);
                //if we have the case where we have got the same value then we have to consider 
                //which of the two vectors have max lexicographic sequence beginning from a and b pointers
                //as a max sequence will affect the answer positively and we would be pushing the result
                //from the vector which has a max sequence
                else{
                    if(greater(max1,max2,a,b)) {
                    //if max1 is greater then we enter
                        merged.push_back(max1[a++]);
                    }
                    else {
                        merged.push_back(max2[b++]);
                    }
                }
            }
            if(merged > ans)
                ans = merged;
        }
        return ans;
    }
};
