class Solution {
public:
    int n,m;
    vector<int>greatest(vector<int>nums, int k)
    {
        int leave=nums.size()-k;
        vector<int>great;
        for(auto i : nums)
        {
           while(leave && great.size() && great.back()<i)
           {
              great.pop_back();
               leave--;
           }
           great.push_back(i); 
        }
        great.resize(k);
        return great;
    }
    bool check(vector<int>nums1, int i, vector<int>nums2, int j)
    {
        while(i<nums1.size() && j<nums2.size() && nums1[i]==nums2[j])
        {
            i++;
            j++;
        }
        if(j==nums2.size()) return true;
        if(i<nums1.size() && nums1[i]>nums2[j]) return true;
        return false;
    }
    // merge the 2 big numbers we have got in 2 vectors into 1 big possible number of size k 
    vector<int> merge(vector<int>nums1 , vector<int>nums2,int k)
    {
        int i=0,j=0;
        vector<int>great(k,0);
        for(int l=0;l<k;l++)
        {
            great[l]=check(nums1,i,nums2,j)?nums1[i++]:nums2[j++];
        }
        return great;
    }
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        n=nums1.size();
        m=nums2.size();
        vector<int>res;
        for(int i=max(0,k-m);i<=min(k,n);i++)
        {
            //v1 = greatest possible number stored in vector of length i
            //v2 = greatest possible number stored in vector of length k-i;
            auto v1 =greatest(nums1,i);
            auto v2=greatest(nums2,k-i);
            auto candidate=merge(v1,v2,k);
            if(check(candidate,0,res,0))
                res=candidate;
        }
        return res;
    }
};
